import { useState, useEffect } from 'react'
import './App.css'
import axios from 'axios';
// import Card from './components/card/card';
// import Image from './components/image/image';

interface Fruit {
  id: number,
  picture: string,
  name: string
}

function App() {
  const [cards, setCards] = useState<Fruit[]>([]);
  const [addFormIsShown, setAddFormIsShown] = useState<boolean>(false);
  const [addName, setAddName] = useState<string>('');
  const [addLink, setAddLink] = useState<string>('');

  useEffect(() => {
    axios.get<Fruit[]>('http://localhost:3000/fruits').then(({ data }) => {
      setCards(data)
      console.log(data)
    });
  }, [])

  if (!cards) return null;

  const showAddForm = () => {
    setAddFormIsShown(current => !current);
  }

  const addNewFruit = () => {
    axios
      .post('http://localhost:3000/fruits', {
        picture: 'data:image/jpeg;base64,',
        name: 'passionfruit'
      })
      .then((response) => {
        console.log(response.data)
        setCards(response.data);
      })
  }

  const deleteCard = (id: number, e: any) => {
    e.preventDefault();
    axios
      .delete<Fruit>(`http://localhost:3000/fruits/${id}`)
      .then(res => {
        console.log('deleted', res)
        const filteredCards = cards.filter(card => card.id !== id);
        setCards(filteredCards);
      })
  }

  return (
    
    <div className='App'>
      <header className='header'>
        <h1 className='header__title'>Fruits</h1>
      </header>
      <section className='cards'>
          {cards.map((card) => {
            return (
              <div className='card__container'>
                <div 
                  className='card'
                  key={card.id}>
                  <img
                    className='card__image'
                    src={card.picture}>
                  </img>
                  <h4
                    className='card__name'>
                    {card.name}
                  </h4>
                  <div className='buttons'>

                    <button
                      className='btn btn__add--show'
                      type='button'
                      onClick={showAddForm}>
                      Add
                    </button>
                    <button
                      className='btn btn__delete--show'
                      onClick={(e) => deleteCard(card.id, e)}
                      >
                      Delete
                    </button>

                  </div>

                    {addFormIsShown && (
                      <form
                        className='card__form form-add'
                        onSubmit={(e) => {
                          e.preventDefault()
                        }}>
                        <label
                          className='label form__label--img'>
                            image link:
                          <input
                            className='input form__input-img'
                            type='text'
                            placeholder='add image link'
                            onChange={(e) => setAddLink(e.target.value)}
                            >
                          </input>
                        </label>
                        <label
                          className='label form__label--name'>
                            name:
                          <input
                            className='input form__input--name'
                            type='text'
                            placeholder='add name'
                            onChange={(e) => setAddName(e.target.value)}
                            >
                          </input>
                        </label>
                        <button
                          className='btn btn__add--submit'
                          type='submit'
                          onClick={addNewFruit}
                          >
                          ADD
                        </button>
                      </form>
                    )}
                    
                </div>
              </div>
            )
          })}
      </section>
    </div>
  )
}

export default App





